              {categories.map((cat) => (
                <button
                  key={cat}
                  onClick={() => setCategory(cat)}
                  className={`px-3 py-1 rounded-full text-xs font-outfit transition-colors ${
                    category === cat
                      ? 'bg-emerald-500/30 text-emerald-300'
                      : 'bg-white/5 text-emerald-300/50 hover:bg-white/10'
                  }`}
                >
                  {cat.replace('_', ' ')}
